      // Create a date object in the specified timezone
      const formatter = new Intl.DateTimeFormat("en-US", {
        year: "numeric",
        month: "2-digit",
        day: "2-digit",
        hour: "2-digit",
        minute: "2-digit",
        hour12: timeFormat === "12",
        timeZone: timezone,
      });

      const localDate = new Date(
        year,
        month,
        day,
        parseInt(hours),
        parseInt(minutes)
      );
      return formatter.format(localDate);
