import React, { useState, useEffect } from "react";
import {
  Container,
  TextField,
  Button,
  Typography,
  Box,
  Paper,
  Grid,
  ThemeProvider,
  createTheme,
  CssBaseline,
  IconButton,
  CircularProgress,
  Autocomplete,
  InputAdornment,
  ToggleButton,
  ToggleButtonGroup,
} from "@mui/material";
import Brightness4Icon from "@mui/icons-material/Brightness4";
import Brightness7Icon from "@mui/icons-material/Brightness7";
import SearchIcon from "@mui/icons-material/Search";
import LocationOnIcon from "@mui/icons-material/LocationOn";
import WatchIcon from "@mui/icons-material/Watch";
import AccessTimeIcon from "@mui/icons-material/AccessTime";
import PublicIcon from "@mui/icons-material/Public";
import ArrowForwardIcon from "@mui/icons-material/ArrowForward";
import GitHubIcon from "@mui/icons-material/GitHub";
import LinkedInIcon from "@mui/icons-material/LinkedIn";
import TwitterIcon from "@mui/icons-material/Twitter";
import EmailIcon from "@mui/icons-material/Email";
import axios from "axios";
import styled from "@emotion/styled";
import { keyframes } from "@emotion/react";

const API_URL = "http://localhost:8000";

const WatchFace = styled.div`
  width: 300px;
  height: 300px;
  border: 15px solid ${(props) => props.theme.palette.primary.main};
  border-radius: 50%;
  position: relative;
  background: ${(props) => props.theme.palette.background.paper};
  box-shadow: 0 0 30px rgba(0, 0, 0, 0.2);
  transition: all 0.3s ease;
  &:hover {
    transform: scale(1.02);
    box-shadow: 0 0 40px rgba(0, 0, 0, 0.3);
  }
`;

const WatchHand = styled.div`
  position: absolute;
  bottom: 50%;
  left: 50%;
  transform-origin: bottom;
  background: ${(props) => props.theme.palette.text.primary};
  border-radius: 4px;
  transition: transform 0.5s cubic-bezier(0.4, 2.08, 0.55, 0.44);
`;

const HourHand = styled(WatchHand)`
  width: 8px;
  height: 70px;
  transform: translateX(-50%)
    rotate(${(props) => props.hour * 30 + props.minute * 0.5}deg);
`;

const MinuteHand = styled(WatchHand)`
  width: 6px;
  height: 100px;
  transform: translateX(-50%) rotate(${(props) => props.minute * 6}deg);
`;

const SecondHand = styled(WatchHand)`
  width: 3px;
  height: 120px;
  background: ${(props) => props.theme.palette.error.main};
  transform: translateX(-50%) rotate(${(props) => props.second * 6}deg);
`;

const WatchCenter = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  width: 20px;
  height: 20px;
  background: ${(props) => props.theme.palette.primary.main};
  border-radius: 50%;
  transform: translate(-50%, -50%);
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
  &::after {
    content: "";
    position: absolute;
    top: 50%;
    left: 50%;
    width: 10px;
    height: 10px;
    background: ${(props) => props.theme.palette.error.main};
    border-radius: 50%;
    transform: translate(-50%, -50%);
  }
`;

const WatchNumber = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  text-align: center;
  transform: rotate(${(props) => props.angle}deg);
  span {
    display: inline-block;
    transform: rotate(${(props) => -props.angle}deg);
    font-size: 1.6rem;
    font-weight: bold;
    color: ${(props) => props.theme.palette.text.primary};
    text-shadow: 0 0 5px rgba(0, 0, 0, 0.1);
  }
`;

const DigitalClock = styled.div`
  font-family: "Digital-7", monospace;
  font-size: 4rem;
  color: ${(props) => props.theme.palette.primary.main};
  text-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  .separator {
    animation: blink 1s infinite;
  }
  .ampm {
    font-size: 1.5rem;
    margin-left: 0.5rem;
    color: ${(props) => props.theme.palette.text.secondary};
  }
  @keyframes blink {
    0%,
    100% {
      opacity: 1;
    }
    50% {
      opacity: 0.5;
    }
  }
`;

const StyledPaper = styled(Paper)`
  padding: 2rem;
  border-radius: 15px;
  background: ${(props) => props.theme.palette.background.paper};
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;
  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 12px 40px rgba(0, 0, 0, 0.15);
  }
`;

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const float = keyframes`
  0% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-20px);
  }
  100% {
    transform: translateY(0px);
  }
`;

const LandingPage = styled(Box)`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background: ${(props) => props.theme.palette.background.default};
  padding: 2rem;
  text-align: center;
  position: relative;
  overflow: hidden;

  &::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: ${(props) =>
      props.theme.palette.mode === "dark"
        ? "radial-gradient(circle at center, rgba(25, 118, 210, 0.1) 0%, transparent 70%)"
        : "radial-gradient(circle at center, rgba(25, 118, 210, 0.05) 0%, transparent 70%)"};
    z-index: 0;
  }
`;

const FeatureCard = styled(Paper)`
  padding: 2rem;
  margin: 1rem;
  text-align: center;
  transition: all 0.3s ease;
  background: ${(props) =>
    props.theme.palette.mode === "dark"
      ? "rgba(255, 255, 255, 0.05)"
      : "rgba(255, 255, 255, 0.8)"};
  backdrop-filter: blur(10px);
  border: 1px solid
    ${(props) =>
      props.theme.palette.mode === "dark"
        ? "rgba(255, 255, 255, 0.1)"
        : "rgba(0, 0, 0, 0.1)"};
  &:hover {
    transform: translateY(-10px);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.2);
  }
`;

const GetStartedButton = styled(Button)`
  margin-top: 2rem;
  padding: 1rem 2rem;
  font-size: 1.2rem;
  border-radius: 30px;
  background: linear-gradient(
    45deg,
    ${(props) => props.theme.palette.primary.main},
    ${(props) => props.theme.palette.secondary.main}
  );
  color: white;
  position: relative;
  overflow: hidden;
  z-index: 1;
  animation: ${fadeIn} 1s ease-out;

  &::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
      45deg,
      ${(props) => props.theme.palette.primary.dark},
      ${(props) => props.theme.palette.secondary.dark}
    );
    z-index: -1;
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  &:hover::before {
    opacity: 1;
  }
`;

const FloatingIcon = styled(Box)`
  position: absolute;
  opacity: 0.1;
  animation: ${float} 6s ease-in-out infinite;
  z-index: 0;
`;

const ContentWrapper = styled(Box)`
  position: relative;
  z-index: 1;
  animation: ${fadeIn} 1s ease-out;
`;

const Logo = styled(Box)`
  display: flex;
  align-items: center;
  gap: 10px;

  .logo-circle {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background: linear-gradient(
      45deg,
      ${(props) => props.theme.palette.primary.main},
      ${(props) => props.theme.palette.secondary.main}
    );
    display: flex;
    align-items: center;
    justify-content: center;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  }

  .logo-text {
    font-weight: bold;
    background: linear-gradient(
      45deg,
      ${(props) => props.theme.palette.primary.main},
      ${(props) => props.theme.palette.secondary.main}
    );
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
  }
`;

const Footer = styled(Box)`
  padding: 2rem;
  background: ${(props) =>
    props.theme.palette.mode === "dark"
      ? "rgba(30, 30, 30, 0.8)"
      : "rgba(245, 245, 245, 0.8)"};
  backdrop-filter: blur(10px);
  margin-top: auto;
  width: 100%;

  .social-icons {
    display: flex;
    justify-content: center;
    gap: 1rem;
    margin-bottom: 1rem;
  }

  .social-icon {
    transition: all 0.3s ease;
    color: ${(props) => props.theme.palette.text.secondary};
    &:hover {
      color: ${(props) => props.theme.palette.primary.main};
      transform: translateY(-5px);
    }
  }
`;

function App() {
  const [darkMode, setDarkMode] = useState(true);
  const [showLanding, setShowLanding] = useState(true);
  const [location, setLocation] = useState("");
  const [timeData, setTimeData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [timezones, setTimezones] = useState([]);
  const [cities, setCities] = useState([]);
  const [clockType, setClockType] = useState("analog");
  const [timeFormat, setTimeFormat] = useState("24");

  const theme = createTheme({
    palette: {
      mode: darkMode ? "dark" : "light",
      primary: {
        main: darkMode ? "#90caf9" : "#1976d2",
      },
    },
    typography: {
      fontFamily: "'Poppins', sans-serif",
    },
  });

  useEffect(() => {
    const fetchTimezones = async () => {
      try {
        const response = await axios.get(`${API_URL}/timezones`);
        setTimezones(response.data.timezones);
        setCities(response.data.cities);
      } catch (err) {
        console.error("Error fetching timezones:", err);
      }
    };
    fetchTimezones();
  }, []);

  useEffect(() => {
    let eventSource;
    if (location) {
      eventSource = new EventSource(`${API_URL}/time-stream/${location}`);
      eventSource.onmessage = (event) => {
        const data = JSON.parse(event.data);
        setTimeData(data);
      };
      eventSource.onerror = () => {
        eventSource.close();
      };
    }
    return () => {
      if (eventSource) {
        eventSource.close();
      }
    };
  }, [location]);

  const handleTimeSearch = async () => {
    setLoading(true);
    setError("");
    try {
      const response = await axios.get(`${API_URL}/time/${location}`);
      setTimeData(response.data);
    } catch (err) {
      setError(
        "Location not found. Please try a city name (e.g., 'New York') or timezone (e.g., 'America/New_York')"
      );
    }
    setLoading(false);
  };

  const handleClockTypeChange = (event, newType) => {
    if (newType !== null) {
      setClockType(newType);
    }
  };

  const handleTimeFormatChange = (event, newFormat) => {
    if (newFormat !== null) {
      setTimeFormat(newFormat);
    }
  };

  const options = [
    ...cities.map((city) => ({ label: city, type: "city" })),
    ...timezones.map((tz) => ({ label: tz, type: "timezone" })),
  ];

  const formatDigitalTime = (timeData) => {
    if (!timeData) return "";
    const [date, time] = timeData.current_time.split(" ");
    let [hours, minutes, seconds] = time.split(":");

    let ampm = "";
    if (timeFormat === "12") {
      const hourNum = parseInt(hours);
      ampm = hourNum >= 12 ? "PM" : "AM";
      hours = hourNum % 12 || 12;
      hours = hours.toString().padStart(2, "0");
    }

    return (
      <DigitalClock>
        <span>{hours}</span>
        <span className="separator">:</span>
        <span>{minutes}</span>
        <span className="separator">:</span>
        <span>{seconds}</span>
        {timeFormat === "12" && <span className="ampm">{ampm}</span>}
      </DigitalClock>
    );
  };

  const handleGetStarted = () => {
    setShowLanding(false);
  };

  if (showLanding) {
    return (
      <ThemeProvider theme={theme}>
        <CssBaseline />
        <LandingPage>
          <Box
            sx={{
              position: "absolute",
              top: "20px",
              right: "20px",
              zIndex: 10,
            }}
          >
            <IconButton
              onClick={() => setDarkMode(!darkMode)}
              color="inherit"
              sx={{
                backgroundColor:
                  theme.palette.mode === "dark"
                    ? "rgba(255,255,255,0.1)"
                    : "rgba(0,0,0,0.05)",
                padding: "12px",
                transition: "all 0.3s ease",
                "&:hover": {
                  transform: "rotate(180deg)",
                  backgroundColor:
                    theme.palette.mode === "dark"
                      ? "rgba(255,255,255,0.2)"
                      : "rgba(0,0,0,0.1)",
                },
              }}
            >
              {darkMode ? <Brightness7Icon /> : <Brightness4Icon />}
            </IconButton>
          </Box>

          <Box
            sx={{ position: "absolute", top: "20px", left: "20px", zIndex: 10 }}
          >
            <Logo>
              <div className="logo-circle">
                <PublicIcon sx={{ color: "white" }} />
              </div>
              <Typography variant="h6" className="logo-text">
                TimezoneBuddy
              </Typography>
            </Logo>
          </Box>

          <FloatingIcon sx={{ top: "10%", left: "10%", animationDelay: "0s" }}>
            <PublicIcon sx={{ fontSize: 100 }} />
          </FloatingIcon>
          <FloatingIcon sx={{ top: "20%", right: "10%", animationDelay: "2s" }}>
            <WatchIcon sx={{ fontSize: 80 }} />
          </FloatingIcon>
          <FloatingIcon
            sx={{ bottom: "20%", left: "15%", animationDelay: "4s" }}
          >
            <AccessTimeIcon sx={{ fontSize: 90 }} />
          </FloatingIcon>

          <ContentWrapper>
            <Box sx={{ mb: 4 }}>
              <Typography
                variant="h2"
                component="h1"
                gutterBottom
                sx={{
                  fontWeight: "bold",
                  background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
                  WebkitBackgroundClip: "text",
                  WebkitTextFillColor: "transparent",
                  mb: 2,
                  fontSize: { xs: "2.5rem", md: "3.5rem" },
                }}
              >
                TimezoneBuddy
              </Typography>
              <Typography
                variant="h5"
                color="text.secondary"
                sx={{
                  mb: 4,
                  maxWidth: "600px",
                  margin: "0 auto",
                }}
              >
                Your global time companion for seamless timezone management
              </Typography>
            </Box>

            <Grid
              container
              spacing={4}
              sx={{ mb: 6, maxWidth: "1200px", margin: "0 auto" }}
            >
              <Grid item xs={12} md={4}>
                <FeatureCard elevation={3}>
                  <PublicIcon
                    sx={{
                      fontSize: 60,
                      color: "primary.main",
                      mb: 2,
                      animation: `${float} 3s ease-in-out infinite`,
                      animationDelay: "0s",
                    }}
                  />
                  <Typography
                    variant="h6"
                    gutterBottom
                    sx={{ fontWeight: "bold" }}
                  >
                    Global Time Lookup
                  </Typography>
                  <Typography color="text.secondary">
                    Instantly check the current time in any city or timezone
                    around the world
                  </Typography>
                </FeatureCard>
              </Grid>
              <Grid item xs={12} md={4}>
                <FeatureCard elevation={3}>
                  <WatchIcon
                    sx={{
                      fontSize: 60,
                      color: "primary.main",
                      mb: 2,
                      animation: `${float} 3s ease-in-out infinite`,
                      animationDelay: "1s",
                    }}
                  />
                  <Typography
                    variant="h6"
                    gutterBottom
                    sx={{ fontWeight: "bold" }}
                  >
                    Beautiful Clocks
                  </Typography>
                  <Typography color="text.secondary">
                    Choose between elegant analog and digital clock displays
                  </Typography>
                </FeatureCard>
              </Grid>
              <Grid item xs={12} md={4}>
                <FeatureCard elevation={3}>
                  <AccessTimeIcon
                    sx={{
                      fontSize: 60,
                      color: "primary.main",
                      mb: 2,
                      animation: `${float} 3s ease-in-out infinite`,
                      animationDelay: "2s",
                    }}
                  />
                  <Typography
                    variant="h6"
                    gutterBottom
                    sx={{ fontWeight: "bold" }}
                  >
                    Time Format Options
                  </Typography>
                  <Typography color="text.secondary">
                    Switch between 12-hour and 24-hour time formats
                  </Typography>
                </FeatureCard>
              </Grid>
            </Grid>

            <GetStartedButton
              variant="contained"
              endIcon={<ArrowForwardIcon />}
              onClick={handleGetStarted}
              sx={{
                "&:hover": {
                  transform: "scale(1.05)",
                },
              }}
            >
              Get Started
            </GetStartedButton>
          </ContentWrapper>

          <Footer>
            <Box className="social-icons">
              <IconButton className="social-icon">
                <GitHubIcon />
              </IconButton>
              <IconButton className="social-icon">
                <TwitterIcon />
              </IconButton>
              <IconButton className="social-icon">
                <LinkedInIcon />
              </IconButton>
              <IconButton className="social-icon">
                <EmailIcon />
              </IconButton>
            </Box>
            <Typography variant="body2" align="center" color="text.secondary">
               {new Date().getFullYear()} TimezoneBuddy | All Rights Reserved
            </Typography>
            <Typography
              variant="caption"
              align="center"
              display="block"
              color="text.secondary"
              sx={{ mt: 1 }}
            >
              Made with  for global collaboration
            </Typography>
          </Footer>
        </LandingPage>
      </ThemeProvider>
    );
  }

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box
        sx={{ minHeight: "100vh", display: "flex", flexDirection: "column" }}
      >
        <Container maxWidth="md" sx={{ flex: 1 }}>
          <Box sx={{ my: 4 }}>
            <Box
              sx={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                mb: 4,
              }}
            >
              <Logo>
                <div className="logo-circle">
                  <PublicIcon sx={{ color: "white" }} />
                </div>
                <Typography variant="h6" className="logo-text">
                  TimezoneBuddy
                </Typography>
              </Logo>
              <IconButton
                onClick={() => setDarkMode(!darkMode)}
                color="inherit"
                sx={{
                  transition: "transform 0.3s ease",
                  "&:hover": {
                    transform: "rotate(180deg)",
                  },
                }}
              >
                {darkMode ? <Brightness7Icon /> : <Brightness4Icon />}
              </IconButton>
            </Box>

            <StyledPaper elevation={3}>
              <Typography
                variant="h6"
                gutterBottom
                sx={{
                  fontWeight: "bold",
                  mb: 3,
                  display: "flex",
                  alignItems: "center",
                  gap: 1,
                }}
              >
                <LocationOnIcon color="primary" />
                Timezone Lookup
              </Typography>
              <Box sx={{ display: "flex", gap: 2, mb: 3 }}>
                <Autocomplete
                  fullWidth
                  options={options}
                  value={
                    location ? { label: location, type: "location" } : null
                  }
                  onChange={(event, newValue) =>
                    setLocation(newValue?.label || "")
                  }
                  groupBy={(option) => option.type}
                  getOptionLabel={(option) => option.label}
                  renderInput={(params) => (
                    <TextField
                      {...params}
                      label="Enter city or timezone"
                      variant="outlined"
                      InputProps={{
                        ...params.InputProps,
                        startAdornment: (
                          <InputAdornment position="start">
                            <SearchIcon color="primary" />
                          </InputAdornment>
                        ),
                      }}
                    />
                  )}
                />
                <Button
                  variant="contained"
                  onClick={handleTimeSearch}
                  disabled={loading || !location}
                  sx={{
                    minWidth: "120px",
                    height: "56px",
                    borderRadius: "8px",
                  }}
                >
                  {loading ? <CircularProgress size={24} /> : "Search"}
                </Button>
              </Box>
              {error && (
                <Typography color="error" sx={{ mb: 2 }}>
                  {error}
                </Typography>
              )}
              {timeData && (
                <>
                  <Box
                    sx={{
                      display: "flex",
                      justifyContent: "center",
                      gap: 2,
                      mb: 3,
                    }}
                  >
                    <ToggleButtonGroup
                      value={clockType}
                      exclusive
                      onChange={handleClockTypeChange}
                      aria-label="clock type"
                    >
                      <ToggleButton value="analog" aria-label="analog clock">
                        <WatchIcon sx={{ mr: 1 }} />
                        Analog
                      </ToggleButton>
                      <ToggleButton value="digital" aria-label="digital clock">
                        <AccessTimeIcon sx={{ mr: 1 }} />
                        Digital
                      </ToggleButton>
                    </ToggleButtonGroup>
                    {clockType === "digital" && (
                      <ToggleButtonGroup
                        value={timeFormat}
                        exclusive
                        onChange={handleTimeFormatChange}
                        aria-label="time format"
                      >
                        <ToggleButton value="12" aria-label="12 hour format">
                          12H
                        </ToggleButton>
                        <ToggleButton value="24" aria-label="24 hour format">
                          24H
                        </ToggleButton>
                      </ToggleButtonGroup>
                    )}
                  </Box>
                  <Grid container spacing={4} alignItems="center">
                    <Grid item xs={12} md={6}>
                      <Box
                        sx={{
                          display: "flex",
                          justifyContent: "center",
                          alignItems: "center",
                          minHeight: "300px",
                        }}
                      >
                        {clockType === "analog" ? (
                          <WatchFace>
                            {[...Array(12)].map((_, i) => (
                              <WatchNumber key={i} angle={i * 30}>
                                <span>{i + 1}</span>
                              </WatchNumber>
                            ))}
                            <HourHand
                              hour={timeData.hour}
                              minute={timeData.minute}
                            />
                            <MinuteHand minute={timeData.minute} />
                            <SecondHand second={timeData.second} />
                            <WatchCenter />
                          </WatchFace>
                        ) : (
                          formatDigitalTime(timeData)
                        )}
                      </Box>
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <Box
                        sx={{
                          textAlign: { xs: "center", md: "left" },
                          p: 2,
                        }}
                      >
                        <Typography
                          variant="h5"
                          gutterBottom
                          sx={{ fontWeight: "bold" }}
                        >
                          {timeData.timezone}
                        </Typography>
                        <Typography
                          variant="h6"
                          gutterBottom
                          sx={{ color: "text.secondary" }}
                        >
                          Current Time: {timeData.current_time}
                        </Typography>
                        <Typography
                          variant="body1"
                          sx={{
                            color: "text.secondary",
                            display: "inline-block",
                            p: 1,
                            borderRadius: "4px",
                            bgcolor: "action.hover",
                          }}
                        >
                          UTC Offset: {timeData.utc_offset}
                        </Typography>
                      </Box>
                    </Grid>
                  </Grid>
                </>
              )}
            </StyledPaper>
          </Box>
        </Container>

        <Footer>
          <Box className="social-icons">
            <IconButton className="social-icon">
              <GitHubIcon />
            </IconButton>
            <IconButton className="social-icon">
              <TwitterIcon />
            </IconButton>
            <IconButton className="social-icon">
              <LinkedInIcon />
            </IconButton>
            <IconButton className="social-icon">
              <EmailIcon />
            </IconButton>
          </Box>
          <Typography variant="body2" align="center" color="text.secondary">
             {new Date().getFullYear()} TimezoneBuddy | All Rights Reserved
          </Typography>
          <Typography
            variant="caption"
            align="center"
            display="block"
            color="text.secondary"
            sx={{ mt: 1 }}
          >
            Made with  for global collaboration
          </Typography>
        </Footer>
      </Box>
    </ThemeProvider>
  );
}

export default App;
