    const checkMeetingReminders = () => {
      const now = new Date();

      savedMeetings.forEach((meeting) => {
        if (meeting.reminderSent) return;

        // Convert meeting date string to Date object correctly
        const meetingDateTime = new Date(meeting.date);
        if (isNaN(meetingDateTime.getTime())) {
          console.error("Invalid meeting date", meeting.date);
          return;
        }

        const [hours, minutes] = meeting.time
          .split(":")
          .map((num) => parseInt(num));
        meetingDateTime.setHours(hours, minutes, 0);

        const timeDiff = meetingDateTime.getTime() - now.getTime();
        const minutesDiff = Math.floor(timeDiff / (1000 * 60));

        // Check if meeting is within the next 15 minutes and not in the past
        if (timeDiff > 0 && minutesDiff <= 15) {
          if (Notification.permission === "granted") {
            try {
              const notification = new Notification(
                `Meeting Reminder: ${meeting.title}`,
                {
                  body: `Your meeting "${meeting.title}" starts in ${minutesDiff} minutes`,
                  icon: "/favicon.ico",
                  requireInteraction: true,
                }
              );

              notification.onclick = () => {
                window.focus();
                notification.close();
              };

              // Mark as reminder sent
              setSavedMeetings((prevMeetings) =>
                prevMeetings.map((m) =>
                  m.id === meeting.id ? { ...m, reminderSent: true } : m
                )
              );

              console.log(`Notification sent for meeting: ${meeting.title}`);
            } catch (error) {
              console.error("Error sending notification:", error);
            }
          } else {
            console.log("Notification permission not granted");
            requestNotificationPermission();
          }
        }
      });
    };

    console.log("Setting up meeting reminders check interval");
    const intervalId = setInterval(checkMeetingReminders, 30000); // Check every 30 seconds for testing

    // Run once immediately to check for pending notifications
