import React, { useState, useEffect } from "react";
import {
  Container,
  TextField,
  Button,
  Typography,
  Box,
  Paper,
  Grid,
  ThemeProvider,
  createTheme,
  CssBaseline,
  IconButton,
  CircularProgress,
  Autocomplete,
} from "@mui/material";
import Brightness4Icon from "@mui/icons-material/Brightness4";
import Brightness7Icon from "@mui/icons-material/Brightness7";
import axios from "axios";
import styled from "@emotion/styled";

const API_URL = "http://localhost:8000";

const WatchFace = styled.div`
  width: 200px;
  height: 200px;
  border: 8px solid ${(props) => props.theme.palette.primary.main};
  border-radius: 50%;
  position: relative;
  background: ${(props) => props.theme.palette.background.paper};
  box-shadow: 0 0 20px rgba(0, 0, 0, 0.1);
`;

const WatchHand = styled.div`
  position: absolute;
  bottom: 50%;
  left: 50%;
  transform-origin: bottom;
  background: ${(props) => props.theme.palette.text.primary};
  border-radius: 4px;
`;

const HourHand = styled(WatchHand)`
  width: 4px;
  height: 50px;
  transform: translateX(-50%)
    rotate(${(props) => props.hour * 30 + props.minute * 0.5}deg);
`;

const MinuteHand = styled(WatchHand)`
  width: 3px;
  height: 70px;
  transform: translateX(-50%) rotate(${(props) => props.minute * 6}deg);
`;

const SecondHand = styled(WatchHand)`
  width: 2px;
  height: 80px;
  background: ${(props) => props.theme.palette.error.main};
  transform: translateX(-50%) rotate(${(props) => props.second * 6}deg);
`;

const WatchCenter = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  width: 12px;
  height: 12px;
  background: ${(props) => props.theme.palette.primary.main};
  border-radius: 50%;
  transform: translate(-50%, -50%);
`;

const WatchNumber = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  text-align: center;
  transform: rotate(${(props) => props.angle}deg);
  span {
    display: inline-block;
    transform: rotate(${(props) => -props.angle}deg);
    font-size: 1.2rem;
    font-weight: bold;
  }
`;

function App() {
  const [darkMode, setDarkMode] = useState(false);
  const [location, setLocation] = useState("");
  const [timeData, setTimeData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [timezones, setTimezones] = useState([]);
  const [cities, setCities] = useState([]);

  const theme = createTheme({
    palette: {
      mode: darkMode ? "dark" : "light",
    },
  });

  useEffect(() => {
    const fetchTimezones = async () => {
      try {
        const response = await axios.get(`${API_URL}/timezones`);
        setTimezones(response.data.timezones);
        setCities(response.data.cities);
      } catch (err) {
        console.error("Error fetching timezones:", err);
      }
    };
    fetchTimezones();
  }, []);

  const handleTimeSearch = async () => {
    setLoading(true);
    setError("");
    try {
      const response = await axios.get(`${API_URL}/time/${location}`);
      setTimeData(response.data);
    } catch (err) {
      setError(
        "Location not found. Please try a city name (e.g., 'New York') or timezone (e.g., 'America/New_York')"
      );
    }
    setLoading(false);
  };

  const options = [
    ...cities.map((city) => ({ label: city, type: "city" })),
    ...timezones.map((tz) => ({ label: tz, type: "timezone" })),
  ];

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="md">
        <Box sx={{ my: 4 }}>
          <Box
            sx={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              mb: 4,
            }}
          >
            <Typography variant="h4" component="h1" gutterBottom>
              TimezoneBuddy
            </Typography>
            <IconButton onClick={() => setDarkMode(!darkMode)} color="inherit">
              {darkMode ? <Brightness7Icon /> : <Brightness4Icon />}
            </IconButton>
          </Box>

          <Paper sx={{ p: 3, mb: 4 }}>
            <Typography variant="h6" gutterBottom>
              Timezone Lookup
            </Typography>
            <Box sx={{ display: "flex", gap: 2, mb: 2 }}>
              <Autocomplete
                fullWidth
                options={options}
                value={location ? { label: location, type: "location" } : null}
                onChange={(event, newValue) =>
                  setLocation(newValue?.label || "")
                }
                groupBy={(option) => option.type}
                getOptionLabel={(option) => option.label}
                renderInput={(params) => (
                  <TextField
                    {...params}
                    label="Enter city or timezone"
                    variant="outlined"
                  />
                )}
              />
              <Button
                variant="contained"
                onClick={handleTimeSearch}
                disabled={loading || !location}
              >
                Search
              </Button>
            </Box>
            {loading && <CircularProgress />}
            {error && <Typography color="error">{error}</Typography>}
            {timeData && (
              <Grid container spacing={4} alignItems="center">
                <Grid item xs={12} md={6}>
                  <Box sx={{ display: "flex", justifyContent: "center" }}>
                    <WatchFace>
                      {[...Array(12)].map((_, i) => (
                        <WatchNumber key={i} angle={i * 30}>
                          <span>{i + 1}</span>
                        </WatchNumber>
                      ))}
                      <HourHand hour={timeData.hour} minute={timeData.minute} />
                      <MinuteHand minute={timeData.minute} />
                      <SecondHand second={timeData.second} />
                      <WatchCenter />
                    </WatchFace>
                  </Box>
                </Grid>
                <Grid item xs={12} md={6}>
                  <Box sx={{ textAlign: { xs: "center", md: "left" } }}>
                    <Typography variant="h5" gutterBottom>
                      {timeData.timezone}
                    </Typography>
                    <Typography variant="h6" gutterBottom>
                      Current Time: {timeData.current_time}
                    </Typography>
                    <Typography variant="body1">
                      UTC Offset: {timeData.utc_offset}
                    </Typography>
                  </Box>
                </Grid>
              </Grid>
            )}
          </Paper>
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
