import React, { useState, useEffect, useRef } from "react";
import { ThemeProvider, createTheme } from "@mui/material/styles";
import CssBaseline from "@mui/material/CssBaseline";
import {
  Container,
  Box,
  Typography,
  Button,
  TextField,
  Switch,
  Paper,
  IconButton,
  Grid,
  Autocomplete,
  Chip,
  FormControlLabel,
  Divider,
  Avatar,
  AppBar,
  Toolbar,
  CircularProgress,
  InputAdornment,
  ToggleButtonGroup,
  ToggleButton,
} from "@mui/material";
import axios from "axios";
import CalendarTodayIcon from "@mui/icons-material/CalendarToday";
import AccessTimeIcon from "@mui/icons-material/AccessTime";
import DeleteIcon from "@mui/icons-material/Delete";
import DarkModeIcon from "@mui/icons-material/DarkMode";
import LightModeIcon from "@mui/icons-material/LightMode";
import AddIcon from "@mui/icons-material/Add";
import PublicIcon from "@mui/icons-material/Public";
import WatchLaterIcon from "@mui/icons-material/WatchLater";
import ScheduleIcon from "@mui/icons-material/Schedule";
import WatchIcon from "@mui/icons-material/Watch";
import PlayArrowIcon from "@mui/icons-material/PlayArrow";
import PauseIcon from "@mui/icons-material/Pause";
import RestartAltIcon from "@mui/icons-material/RestartAlt";
import TimerIcon from "@mui/icons-material/Timer";
import SearchIcon from "@mui/icons-material/Search";
import LocationOnIcon from "@mui/icons-material/LocationOn";
import ArrowForwardIcon from "@mui/icons-material/ArrowForward";
import GitHubIcon from "@mui/icons-material/GitHub";
import TwitterIcon from "@mui/icons-material/Twitter";
import LinkedInIcon from "@mui/icons-material/LinkedIn";
import EmailIcon from "@mui/icons-material/Email";
import NotificationsIcon from "@mui/icons-material/Notifications";
import SaveIcon from "@mui/icons-material/Save";
import Brightness4Icon from "@mui/icons-material/Brightness4";
import Brightness7Icon from "@mui/icons-material/Brightness7";
import styled from "@emotion/styled";
import { keyframes } from "@emotion/react";

const API_URL = "http://localhost:8000";

const WatchFace = styled.div`
  width: 300px;
  height: 300px;
  border: 15px solid ${(props) => props.theme.palette.primary.main};
  border-radius: 50%;
  position: relative;
  background: ${(props) => props.theme.palette.background.paper};
  box-shadow: 0 0 30px rgba(0, 0, 0, 0.2);
  transition: all 0.3s ease;
  &:hover {
    transform: scale(1.02);
    box-shadow: 0 0 40px rgba(0, 0, 0, 0.3);
  }
`;

const WatchHand = styled.div`
  position: absolute;
  bottom: 50%;
  left: 50%;
  transform-origin: bottom;
  background: ${(props) => props.theme.palette.text.primary};
  border-radius: 4px;
  transition: transform 0.5s cubic-bezier(0.4, 2.08, 0.55, 0.44);
`;

const HourHand = styled(WatchHand)`
  width: 8px;
  height: 70px;
  transform: translateX(-50%)
    rotate(${(props) => props.hour * 30 + props.minute * 0.5}deg);
`;

const MinuteHand = styled(WatchHand)`
  width: 6px;
  height: 100px;
  transform: translateX(-50%) rotate(${(props) => props.minute * 6}deg);
`;

const SecondHand = styled(WatchHand)`
  width: 3px;
  height: 120px;
  background: ${(props) => props.theme.palette.error.main};
  transform: translateX(-50%) rotate(${(props) => props.second * 6}deg);
`;

const WatchCenter = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  width: 20px;
  height: 20px;
  background: ${(props) => props.theme.palette.primary.main};
  border-radius: 50%;
  transform: translate(-50%, -50%);
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
  &::after {
    content: "";
    position: absolute;
    top: 50%;
    left: 50%;
    width: 10px;
    height: 10px;
    background: ${(props) => props.theme.palette.error.main};
    border-radius: 50%;
    transform: translate(-50%, -50%);
  }
`;

const WatchNumber = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  text-align: center;
  transform: rotate(${(props) => props.angle}deg);
  span {
    display: inline-block;
    transform: rotate(${(props) => -props.angle}deg);
    font-size: 1.6rem;
    font-weight: bold;
    color: ${(props) => props.theme.palette.text.primary};
    text-shadow: 0 0 5px rgba(0, 0, 0, 0.1);
  }
`;

const DigitalClock = styled.div`
  font-family: "Digital-7", monospace;
  font-size: 4rem;
  color: ${(props) => props.theme.palette.primary.main};
  text-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  .separator {
    animation: blink 1s infinite;
  }
  .ampm {
    font-size: 1.5rem;
    margin-left: 0.5rem;
    color: ${(props) => props.theme.palette.text.secondary};
  }
  @keyframes blink {
    0%,
    100% {
      opacity: 1;
    }
    50% {
      opacity: 0.5;
    }
  }
`;

const StyledPaper = styled(Paper)`
  padding: 2rem;
  border-radius: 15px;
  background: ${(props) => props.theme.palette.background.paper};
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;
  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 12px 40px rgba(0, 0, 0, 0.15);
  }
`;

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const float = keyframes`
  0% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-20px);
  }
  100% {
    transform: translateY(0px);
  }
`;

const LandingPage = styled(Box)`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
