                    "& .MuiAutocomplete-listbox": {
                      "& .MuiAutocomplete-option": {
                        transition: "background-color 0.2s ease",
                        borderLeft: "0px solid transparent",
                        "&:hover": {
                          borderLeft: `4px solid ${theme.palette.primary.main}`,
                        },
                      },
                      "& .MuiAutocomplete-groupLabel": {
                        background:
                          theme.palette.mode === "dark"
                            ? "rgba(255, 255, 255, 0.05)"
                            : "rgba(0, 0, 0, 0.05)",
                        color: theme.palette.primary.main,
                        fontWeight: "bold",
                      },
                    },
                  }}
                />
                <PulseButton
                  variant="contained"
                  onClick={handleTimeSearch}
                  disabled={loading || !location}
                  sx={{
                    height: "56px",
                  }}
                >
                  <span className="button-content">
                    {loading ? (
                      <CircularProgress size={24} color="inherit" />
                    ) : (
                      <>
                        <SearchIcon className="search-icon" />
                        Search
                      </>
                    )}
                  </span>
                </PulseButton>
              </Box>
              {error && (
                <Typography color="error" sx={{ mb: 2 }}>
                  {error}
                </Typography>
              )}
              {timeData && (
                <>
                  <Box
                    sx={{
                      display: "flex",
                      justifyContent: "center",
                      gap: 2,
                      mb: 3,
                    }}
                  >
                    <ToggleButtonGroup
                      value={clockType}
                      exclusive
                      onChange={handleClockTypeChange}
                      aria-label="clock type"
                    >
                      <ToggleButton value="analog" aria-label="analog clock">
                        <WatchIcon sx={{ mr: 1 }} />
                        Analog
                      </ToggleButton>
                      <ToggleButton value="digital" aria-label="digital clock">
                        <AccessTimeIcon sx={{ mr: 1 }} />
                        Digital
                      </ToggleButton>
                    </ToggleButtonGroup>
                    {clockType === "digital" && (
                      <ToggleButtonGroup
                        value={timeFormat}
                        exclusive
                        onChange={handleTimeFormatChange}
                        aria-label="time format"
                      >
                        <ToggleButton value="12" aria-label="12 hour format">
                          12H
                        </ToggleButton>
                        <ToggleButton value="24" aria-label="24 hour format">
                          24H
                        </ToggleButton>
                      </ToggleButtonGroup>
                    )}
                  </Box>
                  <Grid container spacing={4} alignItems="center">
                    <Grid item xs={12} md={6}>
                      <Box
                        sx={{
                          display: "flex",
                          justifyContent: "center",
                          alignItems: "center",
                          minHeight: "300px",
                        }}
                      >
                        {clockType === "analog" ? (
                          <WatchFace>
                            {[...Array(12)].map((_, i) => (
                              <WatchNumber key={i} angle={i * 30}>
                                <span>{i + 1}</span>
                              </WatchNumber>
                            ))}
                            <HourHand
                              hour={timeData.hour}
                              minute={timeData.minute}
                            />
                            <MinuteHand minute={timeData.minute} />
                            <SecondHand second={timeData.second} />
                            <WatchCenter />
                          </WatchFace>
                        ) : (
                          formatDigitalTime(timeData)
                        )}
                      </Box>
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <Box
                        sx={{
                          textAlign: { xs: "center", md: "left" },
                          p: 2,
                        }}
                      >
                        <Typography
                          variant="h5"
                          gutterBottom
                          sx={{ fontWeight: "bold" }}
                        >
                          {timeData.timezone}
                        </Typography>
                        <Typography
                          variant="h6"
                          gutterBottom
                          sx={{ color: "text.secondary" }}
                        >
                          Current Time: {timeData.current_time}
                        </Typography>
                        <Typography
                          variant="body1"
                          sx={{
                            color: "text.secondary",
                            display: "inline-block",
                            p: 1,
                            borderRadius: "4px",
                            bgcolor: "action.hover",
                          }}
                        >
                          UTC Offset: {timeData.utc_offset}
                        </Typography>
                      </Box>
                    </Grid>
                  </Grid>
                </>
              )}

              <Box sx={{ mt: 4, mb: 2 }}>
                <Grid container spacing={2} alignItems="center">
                  <Grid item>
                    <Button
                      onClick={() =>
                        setShowMeetingScheduler(!showMeetingScheduler)
                      }
                      color="primary"
                      variant="outlined"
                      startIcon={
                        showMeetingScheduler ? (
                          <ArrowForwardIcon />
                        ) : (
                          <AccessTimeIcon />
                        )
                      }
                      sx={{
                        borderRadius: "10px",
                        px: 3,
                        py: 1,
                        fontWeight: "medium",
                        transition: "all 0.3s ease",
                        "&:hover": {
                          transform: "translateY(-2px)",
                          boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
                        },
                      }}
                    >
                      {showMeetingScheduler
                        ? "Hide Meeting Scheduler"
                        : "Meeting Scheduler"}
                    </Button>
                  </Grid>
                  <Grid item>
                    <Button
                      onClick={() => setShowStopwatch(!showStopwatch)}
                      color="secondary"
                      variant="outlined"
                      startIcon={<TimerIcon />}
                      sx={{
                        borderRadius: "10px",
                        px: 3,
                        py: 1,
                        fontWeight: "medium",
                        transition: "all 0.3s ease",
                        "&:hover": {
                          transform: "translateY(-2px)",
                          boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
                        },
                      }}
                    >
                      {showStopwatch ? "Hide Stopwatch" : "Stopwatch"}
                    </Button>
                  </Grid>
                  <Grid item>
                    <Button
                      onClick={createDirectNotification}
                      color="warning"
                      variant="outlined"
                      sx={{
                        borderRadius: "10px",
                        px: 3,
                        py: 1,
                        fontWeight: "medium",
                        transition: "all 0.3s ease",
                        "&:hover": {
                          transform: "translateY(-2px)",
                          boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
                        },
                      }}
                    >
                      Test Direct Notification
                    </Button>
                  </Grid>
                </Grid>
              </Box>

              {showStopwatch && (
                <Box
                  sx={{
                    mt: 3,
                    p: 3,
                    bgcolor: "background.default",
                    borderRadius: "12px",
                    textAlign: "center",
                  }}
                >
                  <Typography
                    variant="h6"
                    gutterBottom
                    sx={{ fontWeight: "bold", mb: 3 }}
                  >
                    Stopwatch
                  </Typography>

                  <StopwatchDisplay>
                    {(() => {
                      const time = formatStopwatchTime(elapsedTime);
                      return (
                        <>
                          <span>{time.hours}</span>
                          <span className="separator">:</span>
                          <span>{time.minutes}</span>
                          <span className="separator">:</span>
                          <span>{time.seconds}</span>
                          <span className="separator">.</span>
                          <span className="milliseconds">{time.ms}</span>
                        </>
                      );
                    })()}
                  </StopwatchDisplay>

                  <StopwatchButtons>
                    {!isRunning ? (
                      <StopwatchButton
                        onClick={startStopwatch}
                        maincolor={theme.palette.success.main}
                        hovercolor={theme.palette.success.dark}
                      >
                        <PlayArrowIcon />
                      </StopwatchButton>
                    ) : (
                      <StopwatchButton
                        onClick={pauseStopwatch}
                        maincolor={theme.palette.warning.main}
                        hovercolor={theme.palette.warning.dark}
                      >
                        <PauseIcon />
                      </StopwatchButton>
                    )}
                    <StopwatchButton
                      onClick={resetStopwatch}
                      maincolor={theme.palette.error.main}
                      hovercolor={theme.palette.error.dark}
                    >
                      <RestartAltIcon />
                    </StopwatchButton>
                  </StopwatchButtons>
                </Box>
              )}

              {showMeetingScheduler && (
                <Box
                  sx={{
                    mt: 3,
                    p: 2,
                    bgcolor: "background.default",
                    borderRadius: "12px",
                  }}
                >
                  <Typography
                    variant="h6"
                    gutterBottom
                    sx={{ fontWeight: "bold" }}
                  >
                    Schedule a Meeting Across Timezones
                  </Typography>

                  <Grid container spacing={3} sx={{ mb: 3 }}>
                    <Grid item xs={12}>
                      <TextField
                        label="Meeting Title"
                        fullWidth
                        value={meetingTitle}
                        onChange={(e) => setMeetingTitle(e.target.value)}
                        placeholder="e.g., Weekly Team Standup"
                        sx={{
                          "& .MuiOutlinedInput-root": {
                            borderRadius: "10px",
                          },
                        }}
                      />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                      <TextField
                        label="Date"
                        type="date"
                        fullWidth
                        value={meetingDate.toISOString().split("T")[0]}
                        onChange={(e) =>
                          setMeetingDate(new Date(e.target.value))
                        }
                        InputLabelProps={{ shrink: true }}
                        sx={{
                          "& .MuiOutlinedInput-root": {
                            borderRadius: "10px",
                          },
                        }}
                      />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                      <TextField
                        label="Time"
                        type="time"
                        fullWidth
                        value={meetingTime}
                        onChange={(e) => setMeetingTime(e.target.value)}
                        InputLabelProps={{ shrink: true }}
                        sx={{
                          "& .MuiOutlinedInput-root": {
                            borderRadius: "10px",
                          },
                        }}
                      />
                    </Grid>
                  </Grid>

                  <Typography
                    variant="subtitle1"
                    sx={{ mb: 2, fontWeight: "medium" }}
                  >
                    Add Timezones:
                  </Typography>

                  <Box
                    sx={{ display: "flex", gap: 2, mb: 3, flexWrap: "wrap" }}
                  >
                    <Autocomplete
                      options={timezones}
                      renderInput={(params) => (
                        <TextField
                          {...params}
                          label="Select Timezone"
                          variant="outlined"
                          sx={{ minWidth: "250px" }}
                        />
                      )}
                      onChange={(_, value) => handleAddTimezone(value)}
                      sx={{
                        "& .MuiOutlinedInput-root": {
                          borderRadius: "10px",
                        },
                        "& .MuiAutocomplete-option": {
                          '&[data-option-index*="Asia/Kolkata"]': {
                            backgroundColor:
                              theme.palette.mode === "dark"
                                ? "rgba(255, 64, 129, 0.15)"
                                : "rgba(245, 0, 87, 0.08)",
                            fontWeight: "bold",
                          },
                        },
                      }}
                      renderOption={(props, option) => (
                        <li
                          {...props}
                          style={
                            option === "Asia/Kolkata"
                              ? {
                                  fontWeight: "bold",
                                  backgroundColor:
                                    theme.palette.mode === "dark"
                                      ? "rgba(255, 64, 129, 0.15)"
                                      : "rgba(245, 0, 87, 0.08)",
                                }
                              : {}
                          }
                        >
                          {option}
                        </li>
                      )}
                    />

                    <Box
                      sx={{ mb: 3, display: "flex", flexWrap: "wrap", gap: 1 }}
                    >
                      <Typography
                        variant="body2"
                        sx={{ mr: 1, alignSelf: "center" }}
                      >
                        Quick Add:
                      </Typography>
                      <Button
                        size="small"
                        variant="outlined"
                        onClick={() => handleAddTimezone("Asia/Kolkata")}
                        sx={{
                          borderRadius: "20px",
                          py: 0.5,
                          borderColor: theme.palette.secondary.main,
                          color: theme.palette.secondary.main,
                          "&:hover": {
                            backgroundColor:
                              theme.palette.mode === "dark"
                                ? "rgba(255, 64, 129, 0.15)"
                                : "rgba(245, 0, 87, 0.08)",
                            borderColor: theme.palette.secondary.main,
                          },
                        }}
                      >
                        🇮🇳 India (Asia/Kolkata)
                      </Button>
                      <Button
                        size="small"
                        variant="outlined"
                        onClick={() => handleAddTimezone("America/New_York")}
                        sx={{
                          borderRadius: "20px",
                          py: 0.5,
                        }}
                      >
                        🇺🇸 New York
                      </Button>
                      <Button
                        size="small"
                        variant="outlined"
                        onClick={() => handleAddTimezone("Europe/London")}
                        sx={{
                          borderRadius: "20px",
                          py: 0.5,
                        }}
                      >
                        🇬🇧 London
                      </Button>
                    </Box>

                    <PulseButton
                      onClick={calculateMeetingTimes}
                      disabled={meetingTimezones.length === 0}
                      sx={{ height: "56px" }}
                    >
                      <span className="button-content">
                        <AccessTimeIcon className="search-icon" />
                        Calculate Times
                      </span>
                    </PulseButton>
                  </Box>

                  {meetingTimezones.length > 0 && (
                    <Box sx={{ mb: 3 }}>
                      <Typography variant="subtitle1" sx={{ mb: 1 }}>
                        Selected Timezones:
                      </Typography>
                      <Box
                        sx={{
                          display: "flex",
                          flexWrap: "wrap",
                          gap: 1,
                        }}
                      >
                        {meetingTimezones.map((tz) => (
                          <Chip
                            key={tz}
                            label={tz}
                            onDelete={() => handleRemoveTimezone(tz)}
                            color="primary"
                            variant="outlined"
                            sx={{
                              borderRadius: "8px",
                              transition: "all 0.2s ease",
                              "&:hover": {
                                transform: "translateY(-2px)",
                                boxShadow: "0 3px 5px rgba(0,0,0,0.1)",
                              },
                            }}
                          />
                        ))}
                      </Box>
                    </Box>
                  )}
                  {meetingResults.length > 0 && (
                    <Box sx={{ mt: 3 }}>
                      <Typography
                        variant="subtitle1"
                        sx={{ mb: 2, fontWeight: "medium" }}
                      >
                        Meeting Times:
                      </Typography>
                      <Grid container spacing={2}>
                        {meetingResults.map((result) => (
                          <Grid
                            item
                            xs={12}
                            sm={6}
                            md={4}
                            key={result.timezone}
                          >
                            <Paper
                              elevation={2}
                              sx={{
                                p: 2,
                                borderRadius: "10px",
                                transition: "all 0.3s ease",
                                "&:hover": {
                                  transform: "translateY(-5px)",
                                  boxShadow: "0 8px 15px rgba(0,0,0,0.1)",
                                },
                              }}
                            >
                              <Typography
                                variant="subtitle2"
                                color="primary"
                                sx={{ fontWeight: "bold" }}
                              >
                                {result.timezone}
                              </Typography>
                              <Typography variant="body1">
                                {result.formattedTime}
                              </Typography>
                            </Paper>
                          </Grid>
                        ))}
                      </Grid>
                    </Box>
                  )}

                  {meetingResults.length > 0 && (
                    <Box
                      sx={{
                        mt: 4,
                        display: "flex",
                        justifyContent: "space-between",
                        alignItems: "center",
                      }}
                    >
                      <Button
                        variant="contained"
                        startIcon={<SaveIcon />}
                        onClick={saveMeeting}
                        disabled={!meetingTitle}
                        sx={{
                          borderRadius: "10px",
                          transition: "all 0.3s ease",
                          background: theme.palette.success.main,
                          px: 3,
                          py: 1.2,
                          fontWeight: "bold",
                          "&:hover": {
                            background: theme.palette.success.dark,
                            transform: "translateY(-2px)",
                            boxShadow: "0 6px 12px rgba(0, 0, 0, 0.15)",
                          },
                        }}
                      >
                        Save Meeting
                      </Button>

                      <Box
                        sx={{ display: "flex", alignItems: "center", gap: 2 }}
                      >
                        <Button
                          size="small"
                          variant="outlined"
                          onClick={sendTestNotification}
                          color="secondary"
                          sx={{
                            borderRadius: "10px",
                            "&:hover": {
                              transform: "translateY(-2px)",
                            },
                          }}
                        >
                          Test Notification
                        </Button>
                        <Button
                          size="small"
                          variant="outlined"
                          onClick={createTestMeeting}
                          color="secondary"
                          sx={{
                            borderRadius: "10px",
                            "&:hover": {
                              transform: "translateY(-2px)",
                            },
                          }}
                        >
                          Create 1-min Test Meeting
                        </Button>
                      </Box>
                    </Box>
                  )}

                  {savedMeetings.length > 0 && (
                    <Box sx={{ mt: 4 }}>
                      <Typography
                        variant="h6"
                        gutterBottom
                        sx={{
                          fontWeight: "bold",
                          display: "flex",
                          alignItems: "center",
                          gap: 1,
                        }}
                      >
                        <NotificationsIcon color="primary" />
                        Saved Meetings
                      </Typography>

                      <Box sx={{ mt: 2 }}>
                        {savedMeetings.map((meeting) => {
                          const meetingDate = new Date(meeting.date);
                          const formattedDate = meetingDate.toLocaleDateString(
                            "en-US",
                            {
                              weekday: "short",
                              month: "short",
                              day: "numeric",
                            }
                          );

                          return (
                            <Paper
                              key={meeting.id}
                              elevation={2}
                              sx={{
                                p: 2,
                                mb: 2,
                                borderRadius: "10px",
                                position: "relative",
                                transition: "all 0.3s ease",
                                "&:hover": {
                                  transform: "translateY(-2px)",
                                  boxShadow: "0 8px 15px rgba(0,0,0,0.1)",
                                },
                                borderLeft: `4px solid ${theme.palette.primary.main}`,
                              }}
                            >
                              <Grid container spacing={2} alignItems="center">
                                <Grid item xs={12} sm={6}>
                                  <Typography
                                    variant="subtitle1"
                                    sx={{ fontWeight: "bold" }}
                                  >
                                    {meeting.title}
                                  </Typography>
                                  <Typography
                                    variant="body2"
                                    color="text.secondary"
                                  >
                                    {formattedDate} at {meeting.time}
                                  </Typography>
                                  <Box
                                    sx={{
                                      display: "flex",
                                      flexWrap: "wrap",
                                      gap: 0.5,
                                      mt: 1,
                                    }}
                                  >
                                    {meeting.timezones.map((tz) => (
                                      <Chip
                                        key={tz}
                                        label={tz}
                                        size="small"
                                        variant="outlined"
                                        sx={{ borderRadius: "4px" }}
                                      />
                                    ))}
                                  </Box>
                                </Grid>
                                <Grid
                                  item
                                  xs={12}
                                  sm={6}
                                  sx={{ textAlign: "right" }}
                                >
                                  <IconButton
                                    onClick={() => deleteMeeting(meeting.id)}
                                    color="error"
                                    sx={{
                                      "&:hover": {
                                        background: theme.palette.error.light,
                                        transform: "rotate(10deg) scale(1.1)",
                                      },
                                      transition: "all 0.2s ease",
                                    }}
                                  >
                                    <DeleteIcon />
                                  </IconButton>
                                </Grid>
                              </Grid>
                            </Paper>
                          );
                        })}
                      </Box>
                    </Box>
                  )}
                </Box>
              )}
            </StyledPaper>
          </Box>

          {/* Saved meetings section with the enhanced styling */}
          {savedMeetings.length > 0 && (
            <Box sx={{ mt: 4 }}>
              <Typography
                variant="h6"
                gutterBottom
                sx={{
                  fontWeight: "bold",
                  display: "flex",
                  alignItems: "center",
                  gap: 1,
                  mb: 3,
                }}
              >
                <AccessTimeIcon color="primary" />
                Saved Meetings
              </Typography>

              <Grid container spacing={3}>
                {savedMeetings.map((meeting) => {
                  const meetingDate = new Date(meeting.date);
                  const formattedDate = meetingDate.toLocaleDateString(
                    "en-US",
                    {
                      weekday: "short",
                      month: "short",
                      day: "numeric",
                    }
                  );

                  return (
                    <Grid item xs={12} sm={6} md={4} key={meeting.id}>
                      <Paper
                        elevation={3}
                        sx={{
                          p: 0,
                          borderRadius: "16px",
                          position: "relative",
                          transition: "all 0.3s ease",
                          overflow: "hidden",
                          height: "100%",
                          display: "flex",
                          flexDirection: "column",
                          "&:hover": {
                            transform: "translateY(-6px)",
                            boxShadow:
                              theme.palette.mode === "dark"
                                ? "0 12px 28px rgba(0,0,0,0.4)"
                                : "0 12px 28px rgba(0,0,0,0.2)",
                          },
                        }}
                      >
                        <Box
                          sx={{
                            p: 2,
                            background: `linear-gradient(120deg, ${theme.palette.primary.main}40, ${theme.palette.secondary.main}30)`,
                            borderBottom: `1px solid ${theme.palette.divider}`,
                          }}
                        >
                          <Typography
                            variant="h6"
                            sx={{
                              fontWeight: "bold",
                              mb: 1,
                              color: theme.palette.text.primary,
                            }}
                          >
                            {meeting.title}
                          </Typography>
                          <Typography
                            variant="body2"
                            sx={{
                              display: "flex",
                              alignItems: "center",
                              gap: 1,
                              color: theme.palette.text.secondary,
                              fontWeight: "medium",
                            }}
                          >
                            <CalendarTodayIcon fontSize="small" />
                            {formattedDate} at {meeting.time}
                          </Typography>
                        </Box>

                        <Box sx={{ p: 2, flexGrow: 1 }}>
                          <Typography
                            variant="subtitle2"
                            sx={{ mb: 1, fontWeight: "medium" }}
                          >
                            Timezones:
                          </Typography>
                          <Box
                            sx={{
                              display: "flex",
                              flexWrap: "wrap",
                              gap: 1,
                            }}
                          >
                            {meeting.timezones.map((tz) => (
                              <Chip
                                key={tz}
                                label={tz}
                                size="small"
                                color="primary"
                                variant="outlined"
                                sx={{
                                  borderRadius: "16px",
                                  fontWeight: "medium",
                                  transition: "all 0.2s ease",
                                  "&:hover": {
                                    backgroundColor: theme.palette.action.hover,
                                    transform: "translateY(-2px)",
                                  },
                                }}
                              />
                            ))}
                          </Box>
                        </Box>

                        <Box
                          sx={{
                            p: 1.5,
                            display: "flex",
                            justifyContent: "flex-end",
                            borderTop: `1px solid ${theme.palette.divider}`,
                            backgroundColor: theme.palette.background.default,
                          }}
                        >
                          <IconButton
                            onClick={() => deleteMeeting(meeting.id)}
                            color="error"
                            size="small"
                            sx={{
                              "&:hover": {
                                backgroundColor:
                                  theme.palette.error.light + "30",
                                transform: "rotate(5deg) scale(1.1)",
                              },
                              transition: "all 0.2s ease",
                            }}
                          >
                            <DeleteIcon />
                          </IconButton>
                        </Box>
                      </Paper>
                    </Grid>
                  );
                })}
              </Grid>
            </Box>
          )}
        </Container>

        <Footer>
          <Box className="social-icons">
            <IconButton className="social-icon">
              <GitHubIcon fontSize="medium" />
            </IconButton>
            <IconButton className="social-icon">
              <TwitterIcon fontSize="medium" />
            </IconButton>
            <IconButton className="social-icon">
              <LinkedInIcon fontSize="medium" />
            </IconButton>
            <IconButton className="social-icon">
              <EmailIcon fontSize="medium" />
            </IconButton>
          </Box>
          <Typography
            variant="body2"
            align="center"
            color="text.secondary"
            sx={{
              fontWeight: 500,
              letterSpacing: 0.5,
              opacity: 0.8,
            }}
          >
            © {new Date().getFullYear()} TimezoneBuddy | All Rights Reserved
          </Typography>
          <Typography
            variant="caption"
            align="center"
            display="block"
            color="text.secondary"
            sx={{
              mt: 1,
              fontSize: "0.8rem",
              fontWeight: 300,
              letterSpacing: 1,
              opacity: 0.7,
            }}
          >
            Made with <span style={{ color: "#ff5c8d" }}>❤️</span> for global
            collaboration
          </Typography>
        </Footer>
      </Box>
    </ThemeProvider>
  );
}

export default App;
